package com.shift;

public class LeftShiftEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
 //  << is used to shift all of the bits in a vlaue to the left side of a specified number of times
		
		System.out.println(10<<2);//10*2^2=10*4=40
		System.out.println(10<<3);//10*2^3=10*8(2*2*2)=80
		System.out.println(20<<2);//20*2^2=20*4=80
System.out.println(20<<5);//20*2^5=20*32(2*2*2*2*2)=640		
		
	}

}
